cd /Users/idris/_Coding_01/AcademyNowProject-Fahrschule && node -e "const fs=require('fs'),path=require('path'); const html=['index.html','danke.html','datenschutz.html','impressum.html']; const css=['css/styles.css','css/mobile.css']; const attr=/\b(?:src|href|content|srcset)\s*=\s*([\"'])(.*?)\1/gi; const url=/url\(([^)]+)\)/gi; const skip=/^(https?:|data:|mailto:|tel:|javascript:|#)/i; const assets=new Set(); function add(raw,base){ if(!raw) return; raw=raw.trim(); if(!raw||skip.test(raw)) return; if(raw.includes(',')){ raw.split(',').forEach(p=>{p=p.trim(); if(!p) return; add(p.split(/\s+/)[0],base);}); return; } raw=raw.split('#')[0].split('?')[0]; if(!raw) return; let rel=raw; if(rel.startsWith('/')) rel=rel.slice(1); const abs=path.resolve(base,rel); if(!abs.startsWith(process.cwd()+path.sep) && abs!==process.cwd()) return; assets.add(path.relative(process.cwd(),abs)); }
for(const f of html){ if(!fs.existsSync(f)) continue; const txt=fs.readFileSync(f,'utf8'); const base=path.dirname(f); let m; while((m=attr.exec(txt))) add(m[2],base); }
for(const f of css){ if(!fs.existsSync(f)) continue; const txt=fs.readFileSync(f,'utf8'); const base=path.dirname(f); let m; while((m=url.exec(txt))) add(m[1].trim().replace(/^['\"]|['\"]$/g,''),base); }
const missing=[...assets].filter(p=>!fs.existsSync(p)).sort(); console.log(`Found ${assets.size} local asset refs`); if(missing.length){ console.error('MISSING FILES:'); missing.forEach(p=>console.error(' - '+p)); process.exit(2);} console.log('OK: all referenced local assets exist.');" && curl -sS -o /dev/null -w "HTTP / => %{http_code}\n" http://localhost:8090/ && curl -sS -o /dev/null -w "HTTP /index.html => %{http_code}\n" http://localhost:8090/index.html

